import React, { useState } from "react";
import { Box, TextField, Button, Typography, Divider, Paper, Link, Stack, Snackbar, Alert} from "@mui/material";
import GoogleIcon from "@mui/icons-material/Google";
import AppleIcon from "@mui/icons-material/Apple";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from 'jwt-decode';

function Login() {
  const [userId, setUserId] = useState(""); 
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const [snackbar, setSnackbar] = useState({
    open: false,
    message: "",
    severity: "info",
  });

  const showMessage = (message, severity = "info") => {
    setSnackbar({ open: true, message, severity });
  };

  const handleCloseSnackbar = () => {
    setSnackbar((prev) => ({ ...prev, open: false }));
  };

  const handleLogin = () => {
    if (!userId || !password) {
      showMessage("아이디와 비밀번호 입력하세요.");
      return;
    }

    fetch("http://localhost:3005/login", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ userId, pwd: password }),
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
        if (data.result === "success") {
          localStorage.setItem("token", data.token);
          const decoded = jwtDecode(data.token);

          console.log("로그인 정보==>", decoded);

          showMessage("로그인 성공", "success");
          setTimeout(() => {
            navigate("/feedList");
          }, 1500);
        } else {
          showMessage(data.message, "error");
        }
      })
      .catch(err => {
        console.error(err);
        showMessage("서버 오류 발생", "error");
      });
  };

  const handleSocialLogin = (type) => {
    showMessage(type + " 로그인은 준비 중입니다.", "warning");
  };

  return (
    <Box
      sx={{
        backgroundColor: "#fafafa",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <Paper
        elevation={3}
        sx={{
          width: 350,
          padding: 4,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography
          sx={{
            fontFamily: `'Lobster', cursive`,
            fontSize: "3rem",
            color: "#262626",
            mb: 3,
          }}
        >
          Instagram
        </Typography>

        <TextField
          fullWidth
          variant="outlined"
          label="아이디"
          size="small"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          sx={{ mb: 2 }}
        />
        <TextField
          fullWidth
          variant="outlined"
          type="password"
          label="비밀번호"
          size="small"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          sx={{ mb: 2 }}
        />

        <Button
          fullWidth
          variant="contained"
          sx={{ mb: 2, backgroundColor: "#0095f6", textTransform: "none" }}
          onClick={handleLogin}
        >
          로그인
        </Button>

        <Divider sx={{ width: "100%", my: 2 }}>또는</Divider>

        <Stack spacing={1} sx={{ width: "100%", mb: 2 }}>
          <Button
            fullWidth
            variant="contained"
            startIcon={<GoogleIcon />}
            onClick={() => handleSocialLogin("Google")}
            sx={{
              backgroundColor: "#4285F4",
              color: "#fff",
              textTransform: "none",
              "&:hover": { backgroundColor: "#357ae8" },
            }}
          >
            Google로 로그인
          </Button>

          <Button
            fullWidth
            variant="contained"
            startIcon={<AppleIcon />}
            onClick={() => handleSocialLogin("Apple")}
            sx={{
              backgroundColor: "#000",
              color: "#fff",
              textTransform: "none",
              "&:hover": { backgroundColor: "#222" },
            }}
          >
            Apple로 로그인
          </Button>

          <Button
            fullWidth
            variant="outlined"
            startIcon={<PhoneIphoneIcon />}
            onClick={() => handleSocialLogin("전화번호")}
            sx={{
              color: "#34A853",
              borderColor: "#34A853",
              textTransform: "none",
            }}
          >
            전화번호로 로그인
          </Button>
        </Stack>

        <Link href="#" underline="hover" sx={{ fontSize: 12, mb: 2 }}>
          비밀번호를 잊으셨나요?
        </Link>

        <Divider sx={{ width: "100%", my: 2 }} />
        <Typography variant="body2">
          계정이 없으신가요?{" "}
          <Link href="#" underline="hover" sx={{ fontWeight: "bold" }}>
            가입하기
          </Link>
        </Typography>
      </Paper>

      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        onClose={handleCloseSnackbar}
        anchorOrigin={{ vertical: "top", horizontal: "center" }}
      >
        <Alert
          onClose={handleCloseSnackbar}
          severity={snackbar.severity}
          variant="filled"
          sx={{ width: "100%" }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default Login;
