import React, {useEffect, useState} from 'react';
import { Container, Typography, Box, Avatar, Grid, Paper, Dialog ,DialogTitle, DialogContent, Button, DialogActions} from '@mui/material';
import { jwtDecode } from 'jwt-decode';


function MyPage() {
  let [info, setInfo] = useState({userName : "", email : "", intro : "", profileImg : ""});
  let [open, setOpen] = useState(false);
  let [imgUrl, setImgUrl] = useState();
  const [imgFile, setImgFile] = useState();
  const token = localStorage.getItem("token");
  const fnUserInfo = ()=>{
    
    if(!token){
      //다시 로그인 페이지로 이동
      // navigate("/");
    }
    const sessionUser = jwtDecode(token); 
    fetch("http://localhost:3005/member/"+sessionUser.sessionEmail)
    .then(res => res.json())
    .then(data => {
      setInfo(data.info);
    });
  }

  const selectImg = (e)=>{
    const file = e.target.files[0];
    if(file){
      const imgUrl = URL.createObjectURL(file);
      setImgUrl(imgUrl);
      setImgFile(file);
    }
  }

  const handleSave = (e)=>{
    if (!imgFile || !token) return;

    const sessionUser = jwtDecode(token); 
    const email = sessionUser.email;

    const formData = new FormData();
    formData.append("file", imgFile);
    formData.append("email", email);

    fetch("http://localhost:3005/member/uploads", {
      method: "POST",
      body: formData
    })
    .then(res => res.json())
    .then(data => {
      if(data.success){
        setOpen(false);
        setImgUrl(null);
        setImgFile(null);
        fnUserInfo();
      }else{
        alert("실패");
      }
    })
    .catch((err) => {
      console.error(err);
      alert("등록 실패");
    });
  }



  useEffect(()=>{
    fnUserInfo();
  }, [])

  return (
    <Container maxWidth="md">
      <Box
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="flex-start"
        minHeight="100vh"
        sx={{ padding: '20px' }}
      >
        <Paper elevation={3} sx={{ padding: '20px', borderRadius: '15px', width: '100%' }}>
          {/* 프로필 정보 상단 배치 */}
          <Box display="flex" flexDirection="column" alignItems="center" sx={{ marginBottom: 3 }}>
            <Avatar
              alt="프로필 이미지"
              src="https://images.unsplash.com/photo-1551963831-b3b1ca40c98e" // 프로필 이미지 경로
              sx={{ width: 100, height: 100, marginBottom: 2 }}
              onClick={()=>setOpen(!open)}
            />
            <Typography variant="h5">{info.userName}</Typography>
            <Typography variant="body2" color="text.secondary">
              @honggildong
            </Typography>
          </Box>
          <Grid container spacing={2} sx={{ marginTop: 2 }}>
            <Grid item xs={4} textAlign="center">
              <Typography variant="h6">팔로워</Typography>
              <Typography variant="body1">150</Typography>
            </Grid>
            <Grid item xs={4} textAlign="center">
              <Typography variant="h6">팔로잉</Typography>
              <Typography variant="body1">100</Typography>
            </Grid>
            <Grid item xs={4} textAlign="center">
              <Typography variant="h6">게시물</Typography>
              <Typography variant="body1">50</Typography>
            </Grid>
          </Grid>
          <Box sx={{ marginTop: 3 }}>
            <Typography variant="h6">내 소개</Typography>
            <Typography variant="body1">
              {info.intro}
            </Typography>
          </Box>
        </Paper>
        <Dialog open={open}>
          <DialogTitle>이미지 수정!</DialogTitle>
          <DialogContent>
            <label>
              <input onChange={selectImg} type="file" accept="image/*" style={{display : "none"}}></input>
              <Button variant='contained' component="span">이미지 선택</Button>
              {!imgUrl ? " 선택된 파일 없음" : " 이미지 선택 됨"}
            </label>
          </DialogContent>
          {imgUrl && (
            <Box textAlign="center">
              <Typography variant="subtitle2" gutterBottom>미리보기</Typography>
              <Avatar
                alt="미리보기"
                src={imgUrl}
                sx={{ width: 100, height: 100, marginTop: 1, marginLeft : 3 }}
                onClick={()=>{setOpen(!open)}}
              />
            </Box>
          )}
          <DialogActions sx={{ justifyContent: "center", gap: 2 }}>
            <Button variant='contained' onClick={handleSave}>저장</Button>
            <Button variant='outlined' onClick={()=>{
              setOpen(false);
              setImgUrl(null);
            }}>취소</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default MyPage;
