import React, { useState } from "react";
import {TextField,Button,Container,Typography, Box,InputLabel,FormControl,Select,MenuItem, Avatar,IconButton,} from '@mui/material';
import { PhotoCamera } from '@mui/icons-material';



function Register() {
  const [file, setFile] = useState(null);
  let titleRef = useRef();


  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

 const handleChange = (e) => {
    const { name, value } = e.target;
    setFeed((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = () => {
   console.log(titleRef.current);

  }
  return (
    <Container maxWidth="sm">
      <Box
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="flex-start" // 상단 정렬
        minHeight="100vh"
        sx={{ padding: '20px' }} // 배경색 없음
      >
        <Typography variant="h4" gutterBottom>
          등록
        </Typography>

        <FormControl fullWidth margin="normal">
          <InputLabel>카테고리</InputLabel>
          <Select name="category" label="카테고리" onChange={handleChange}>
            <MenuItem value={1}>일상</MenuItem>
            <MenuItem value={2}>여행</MenuItem>
            <MenuItem value={3}>음식</MenuItem>
          </Select>
        </FormControl>

        <TextField 
          label="제목" 
          ref={titleRef}
          onChange={handleChange} 
          variant="outlined" 
          margin="normal" 
          fullWidth 
        />

        <TextField
          label="내용"
          name="content"
          onChange={handleChange} 
          variant="outlined"
          margin="normal"
          fullWidth
          multiline
          rows={4}
        />

        <Box display="flex" alignItems="center" margin="normal" fullWidth>
          <input
            accept="image/*"
            style={{ display: 'none' }}
            id="file-upload"
            type="file"
            onChange={handleFileChange}
          />
          <label htmlFor="file-upload">
            <IconButton color="primary" component="span">
              <PhotoCamera />
            </IconButton>
          </label>
          {file && (
            <Avatar
              alt="첨부된 이미지"
              src={URL.createObjectURL(file)}
              sx={{ width: 56, height: 56, marginLeft: 2 }}
            />
          )}
          <Typography variant="body1" sx={{ marginLeft: 2 }}>
            {file ? file.name : '첨부할 파일 선택'}
          </Typography>
        </Box>

        <Button variant="contained" color="primary" onClick={handleSubmit} fullWidth style={{ marginTop: '20px' }}>
          등록하기
        </Button>
      </Box>
    </Container>
  );
}

export default Register;
