import React, { useState } from "react";
import {
  Box, TextField, Button, Typography, Divider, Paper, Link, Stack,
  Dialog, DialogTitle, DialogContent, DialogActions
} from "@mui/material";
import GoogleIcon from "@mui/icons-material/Google";
import AppleIcon from "@mui/icons-material/Apple";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

function Login() {
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const [dialog, setDialog] = useState({
    open: false,
    message: "",
    severity: "info",
  });

  const showMessage = (message, severity = "info") => {
    setDialog({ open: true, message, severity });
  };

  const handleCloseDialog = () => {
    setDialog((prev) => ({ ...prev, open: false }));
  };

  const handleLogin = () => {
    if (!userId || !password) {
      showMessage("아이디와 비밀번호 입력하세요.");
      return;
    }

    fetch("http://localhost:3005/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ userId, pwd: password }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.result === "success") {
          localStorage.setItem("token", data.token);
          const decoded = jwtDecode(data.token);
          showMessage("로그인 성공", "success");
          setTimeout(() => {
            navigate("/");
          }, 1500);
        } else {
          showMessage(data.message, "error");
        }
      })
      .catch(err => {
        console.error(err);
        showMessage("서버 오류 발생", "error");
      });
  };

  const handleSocialLogin = (type) => {
    showMessage(type + " 로그인은 준비 중입니다.", "warning");
  };

  return (
    <Box
      sx={{
        backgroundColor: "#fafafa",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Paper
        elevation={4}
        sx={{
          width: 360,
          padding: "40px 32px",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography
          sx={{
            fontWeight: "bold",
            fontSize: "1.8rem",
            color: "#262626",
            mb: 3,
          }}
        >
          로그인
        </Typography>

        <TextField
          fullWidth
          variant="outlined"
          label="아이디"
          size="small"
          value={userId}
          onChange={(e) => setUserId(e.target.value)}
          sx={{ mb: 1.5 }}
        />
        <TextField
          fullWidth
          variant="outlined"
          type="password"
          label="비밀번호"
          size="small"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          sx={{ mb: 2 }}
        />

        <Button
          fullWidth
          variant="contained"
          sx={{
            mb: 2,
            height: 40,
            fontSize: "0.95rem",
            backgroundColor: "#3897f0",
            textTransform: "none",
          }}
          onClick={handleLogin}
        >
          로그인
        </Button>

        <Divider sx={{ width: "100%", my: 2, fontSize: "0.85rem" }}>또는</Divider>

        <Stack spacing={1.2} sx={{ width: "100%", mb: 2 }}>
          <Button
            fullWidth
            variant="contained"
            startIcon={<GoogleIcon />}
            onClick={() => handleSocialLogin("Google")}
            sx={{
              height: 40,
              backgroundColor: "#6366f1",
              color: "#fff",
              fontSize: "0.85rem",
              textTransform: "none",
              "&:hover": { backgroundColor: "#4f46e5" },
            }}
          >
            Google로 로그인
          </Button>

          <Button
            fullWidth
            variant="contained"
            startIcon={<AppleIcon />}
            onClick={() => handleSocialLogin("Apple")}
            sx={{
              height: 40,
              backgroundColor: "#000",
              color: "#fff",
              fontSize: "0.85rem",
              textTransform: "none",
              "&:hover": { backgroundColor: "#222" },
            }}
          >
            Apple로 로그인
          </Button>

          <Button
            fullWidth
            variant="outlined"
            startIcon={<PhoneIphoneIcon />}
            onClick={() => handleSocialLogin("전화번호")}
            sx={{
              height: 40,
              fontSize: "0.85rem",
              color: "#10b981",
              borderColor: "#10b981",
              textTransform: "none",
              "&:hover": {
                backgroundColor: "#ecfdf5",
              },
            }}
          >
            전화번호로 로그인
          </Button>
        </Stack>

        <Link href="#" underline="hover" sx={{ fontSize: 12, mb: 2 }}>
          비밀번호를 잊으셨나요?
        </Link>

        <Divider sx={{ width: "100%", my: 1 }} />

        <Typography variant="body2" sx={{ fontSize: "0.85rem" }}>
          계정이 없으신가요?{" "}
          <Link href="#" underline="hover" sx={{ fontWeight: "bold" }}>
            가입하기
          </Link>
        </Typography>
      </Paper>

      {/* 메시지 다이얼로그 */}
      <Dialog open={dialog.open} onClose={handleCloseDialog}>
        <DialogTitle>알림</DialogTitle>
        <DialogContent>
          <Typography>{dialog.message}</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>확인</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Login;
