import React, { useState } from "react";
import {TextField,Button,Container,Typography, Box,InputLabel,FormControl,Select,MenuItem, Avatar,IconButton,} from '@mui/material';
import { PhotoCamera } from '@mui/icons-material';
import { useRef } from "react";
import { jwtDecode } from 'jwt-decode';



function Register() {
  const [file, setFile] = useState(null);
  let titleRef = useRef();
  let ContentRef = useRef();
  const token = localStorage.getItem("token");

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const fnRegister = () => {
    if(!token){
      //다시 로그인 페이지로 이동
      // navigate("/");
    }
    const sessionUser = jwtDecode(token); 
   fetch("http://localhost:3005/sns/feed", {
    method: "POST",
    headers: {
      "Content-Type": "application/json" 
      },
    body: JSON.stringify({
      email : sessionUser.sessionEmail, 
      title : titleRef.current.value, 
      Content : ContentRef.current.value 
    })
  })
    .then(res => res.json())
    .then(data => {
      console.log(data);
    })
  }
  return (
    <Container maxWidth="sm">
      <Box
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="flex-start" // 상단 정렬
        minHeight="100vh"
        sx={{ padding: '20px' }} // 배경색 없음
      >
        <Typography variant="h4" gutterBottom>
          등록
        </Typography>

        <FormControl fullWidth margin="normal">
          <InputLabel>카테고리</InputLabel>
          <Select defaultValue="" label="카테고리" >
            <MenuItem value={1}>일상</MenuItem>
            <MenuItem value={2}>여행</MenuItem>
            <MenuItem value={3}>음식</MenuItem>
          </Select>
        </FormControl>

        <TextField label="제목" inputRef={titleRef} variant="outlined" margin="normal" fullWidth />
        <TextField
          label="내용"
          inputRef={ContentRef}
          variant="outlined"
          margin="normal"
          fullWidth
          multiline
          rows={4}
        />

        <Box display="flex" alignItems="center" margin="normal" fullWidth>
          <input
            accept="image/*"
            style={{ display: 'none' }}
            id="file-upload"
            type="file"
            onChange={handleFileChange}
          />
          <label htmlFor="file-upload">
            <IconButton color="primary" component="span">
              <PhotoCamera />
            </IconButton>
          </label>
          {file && (
            <Avatar
              alt="첨부된 이미지"
              src={URL.createObjectURL(file)}
              sx={{ width: 56, height: 56, marginLeft: 2 }}
            />
          )}
          <Typography variant="body1" sx={{ marginLeft: 2 }}>
            {file ? file.name : '첨부할 파일 선택'}
          </Typography>
        </Box>

        <Button variant="contained" color="primary" onClick={fnRegister} fullWidth style={{ marginTop: '20px' }}>
          등록하기
        </Button>
      </Box>
    </Container>
  );
}

export default Register;
