import React, { useState } from "react";
import {
  TextField, Button, Typography, Divider, Paper, Link, Stack,
  Dialog, DialogTitle, DialogContent, DialogActions
} from "@mui/material";
import GoogleIcon from "@mui/icons-material/Google";
import AppleIcon from "@mui/icons-material/Apple";
import PhoneIphoneIcon from "@mui/icons-material/PhoneIphone";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";

function Login() {
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const [dialog, setDialog] = useState({
    open: false,
    message: "",
    severity: "info",
  });

  const showMessage = (message, severity = "info") => {
    setDialog({ open: true, message, severity });
  };

  const handleCloseDialog = () => {
    setDialog((prev) => ({ ...prev, open: false }));
  };

  const handleLogin = () => {
    if (!userId || !password) {
      showMessage("아이디와 비밀번호 입력하세요.");
      return;
    }

    fetch("http://localhost:3005/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ userId, pwd: password }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.result === "success") {
          localStorage.setItem("token", data.token);
          const decoded = jwtDecode(data.token);

          console.log("로그인 정보==>", decoded);
          
          showMessage("로그인 성공", "success");
          setTimeout(() => {
            navigate("/");
          }, 1500);
        } else {
          showMessage(data.message, "error");
        }
      })
      .catch(err => {
        console.error(err);
        showMessage("서버 오류 발생", "error");
      });
  };

  const handleSocialLogin = (type) => {
    showMessage(type + " 로그인은 준비 중입니다.", "warning");
  };

  return (
    <Paper
      elevation={3}
      sx={{
        position: "fixed",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        width: "600px",
        padding: "48px",
        boxSizing: "border-box",
        overflow: "hidden",
        maxHeight: "95vh",
      }}
    >
      <Typography
        variant="h5"
        sx={{ fontWeight: "bold", mb: 3, textAlign: "center" }}
      >
        로그인
      </Typography>

      <TextField
        fullWidth
        label="아이디"
        variant="outlined"
        size="small"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        sx={{ mb: 2 }}
      />
      <TextField
        fullWidth
        type="password"
        label="비밀번호"
        variant="outlined"
        size="small"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        sx={{ mb: 2 }}
      />

      <Button
        fullWidth
        variant="contained"
        onClick={handleLogin}
        sx={{ mb: 3, height: 44, backgroundColor: "#3897f0", textTransform: "none" }}
      >
        로그인
      </Button>

      <Divider sx={{ my: 2 }}>또는</Divider>

      <Stack spacing={1.2} sx={{ mb: 2 }}>
        <Button
          fullWidth
          variant="contained"
          startIcon={<GoogleIcon />}
          onClick={() => handleSocialLogin("Google")}
          sx={{
            backgroundColor: "#6366f1",
            color: "#fff",
            height: 44,
            textTransform: "none",
          }}
        >
          Google로 로그인
        </Button>

        <Button
          fullWidth
          variant="contained"
          startIcon={<AppleIcon />}
          onClick={() => handleSocialLogin("Apple")}
          sx={{
            backgroundColor: "#000",
            color: "#fff",
            height: 44,
            textTransform: "none",
          }}
        >
          Apple로 로그인
        </Button>

        <Button
          fullWidth
          variant="outlined"
          startIcon={<PhoneIphoneIcon />}
          onClick={() => handleSocialLogin("전화번호")}
          sx={{
            color: "#10b981",
            borderColor: "#10b981",
            height: 44,
            textTransform: "none",
          }}
        >
          전화번호로 로그인
        </Button>
      </Stack>

      <Link
        href="#"
        underline="hover"
        sx={{ fontSize: 13, display: "block", mb: 2, textAlign: "center" }}
      >
        비밀번호를 잊으셨나요?
      </Link>

      <Divider sx={{ my: 2 }} />

      <Typography variant="body2" sx={{ textAlign: "center" }}>
        계정이 없으신가요?{" "}
        <Link href="#" underline="hover" sx={{ fontWeight: "bold" }}>
          가입하기
        </Link>
      </Typography>

      <Dialog open={dialog.open} onClose={handleCloseDialog}>
        <DialogTitle>알림</DialogTitle>
        <DialogContent>
          <Typography>{dialog.message}</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>확인</Button>
        </DialogActions>
      </Dialog>
    </Paper>
  );
}

export default Login;
