import React, {useEffect, useState} from 'react';
import { Container, Typography, Box, Avatar, Grid, Paper, Dialog ,DialogTitle} from '@mui/material';
import { jwtDecode } from 'jwt-decode';


function MyPage() {
  let [info, setInfo] = useState({userName : "", email : "", intro : "", profileImg : ""});
  let [open, setOpen] = useState(false);
  const token = localStorage.getItem("token");
  const fnUserInfo = ()=>{
    if(!token){
      //다시 로그인 페이지로 이동
      // navigate("/");
    }
    const sessionUser = jwtDecode(token); 
    fetch("http://localhost:3005/member/"+sessionUser.sessionemail)
    .then(res => res.json())
    .then(data => {
      setInfo(data.info);
    });
  }

  useEffect(()=>{
    fnUserInfo();
  }, [])

  return (
    <Container maxWidth="md">
      <Box
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="flex-start"
        minHeight="100vh"
        sx={{ padding: '20px' }}
      >
        <Paper elevation={3} sx={{ padding: '20px', borderRadius: '15px', width: '100%' }}>
          {/* 프로필 정보 상단 배치 */}
          <Box display="flex" flexDirection="column" alignItems="center" sx={{ marginBottom: 3 }}>
            <Avatar
              alt="프로필 이미지"
              src="https://images.unsplash.com/photo-1551963831-b3b1ca40c98e" // 프로필 이미지 경로
              sx={{ width: 100, height: 100, marginBottom: 2 }}
              onClick={()=>setOpen(!open)}
            />
            <Typography variant="h5">{info.userName}</Typography>
            <Typography variant="body2" color="text.secondary">
              @honggildong
            </Typography>
          </Box>
          <Grid container spacing={2} sx={{ marginTop: 2 }}>
            <Grid item xs={4} textAlign="center">
              <Typography variant="h6">팔로워</Typography>
              <Typography variant="body1">150</Typography>
            </Grid>
            <Grid item xs={4} textAlign="center">
              <Typography variant="h6">팔로잉</Typography>
              <Typography variant="body1">100</Typography>
            </Grid>
            <Grid item xs={4} textAlign="center">
              <Typography variant="h6">게시물</Typography>
              <Typography variant="body1">50</Typography>
            </Grid>
          </Grid>
          <Box sx={{ marginTop: 3 }}>
            <Typography variant="h6">내 소개</Typography>
            <Typography variant="body1">
              {info.intro}
            </Typography>
          </Box>
        </Paper>
        <Dialog open={open}>
          <DialogTitle>이미지 수정</DialogTitle>
          <DialogContent>
            <input type="file" accept="image/"></input>
          </DialogContent>
        </Dialog>
      </Box>
    </Container>
  );
}

export default MyPage;
